import React, { useState } from "react";


class MyReactMemo extends React.Component {
    componentDidMount() {

      }

      componentDidUpdate(prevProps) {
        if (this.props.children !== prevProps.children) {
          this.keyValue = uuid()()
          // alternative: store keyValue as state to trigger re-render
        }
    }
    render() {

    }
}
