import { useState } from "react";


export const HookUseContext = () => {
  const [count, setCount] = useState(1);
  const [color, setColor] = useState(false);



  return (
    <div>
      <h2 >Context</h2>
      <button onClick={() => setCount((prev) => prev + 1)}> +1 Elem </button>

      <button
        onClick={() => {
          setColor(!color);
        }}>
        Change color
      </button>

    </div>
  );
};
